import { createFeatureSelector, createSelector } from '@ngrx/store';
import { IProductState, productAdapter } from './product.state';
import { getRouterParams } from 'src/app/store/router/router.selector';
import { RouterStateUrl } from 'src/app/store/router/custom-route-serializer';

export const PRODUCT_STATE = 'products';
const getProductState =
  createFeatureSelector<typeof IProductState>(PRODUCT_STATE);

const { selectAll, selectEntities } = productAdapter.getSelectors();

export const getCount = createSelector(getProductState, (state) => state.count);

export const getProducts = createSelector(getProductState, selectAll);

const getProductEntites = createSelector(getProductState, selectEntities);

export const getProductById = createSelector(
  getProductEntites,
  getRouterParams,
  (products, route: RouterStateUrl) => {
    return products ? products[route.params['id']] : null;
  }
);
